import { useNavigate, Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import axiosInstance from '../Auth/AxiosInstance';
import './styles/BasicsForm.css';

const API_URL = process.env.REACT_APP_API_URL; // Access the base URL from environment variable

const AboutYourBusiness = ({onComplete}) => {
  // Define the form state
  const [role, setRole] = useState('');
  const [formData, setFormData] = useState({
    business: '',
    location: '',
    salesVolumeEUR: '',
    resultEUR: '',
    employees: '',
    shareToBeTransferred: '',
    transactionBackground: '',
    productMarketFit: '',
    valueProposition: '',
    profitMargin: '',
    revenue: '',
    cashflow: '',
    customerBase: '',
    companyCulture: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
        try {
            const res = await axiosInstance.get(`${API_URL}/api/profiles`);
            setRole(res.data.user.role);
            setFormData(res.data);
        } catch (err) {
            if (err.response && err.response.status === 404) {
                alert('No profile information found for this user.');
            } else {
                alert('Error retrieving profile data.');
            }
        }
    };

    fetchProfile();
}, []);

  // Handle form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axiosInstance.put(`${API_URL}/api/profiles`, {...formData, completedPages: formData.completedPages.includes("AboutYourBusiness") ? formData.completedPages : [...formData.completedPages, "AboutYourBusiness"]});
      setSuccess('Successfully saved profile');        
      onComplete();

    } catch (error) {
      console.error('Error updating profile:', error);
      setError('Error occurred: ', error.message);
    }
  };

  return (
    <div>
      {error && <div className="error-message">{error}</div>} {/* Display error message */}
      {success && <div className="success-message">{success}</div>} {/* Display success message */}

      <form onSubmit={handleSubmit} className="basics-form">
        <div className="form-group">
          <label htmlFor="business">Business</label>
          <input
            type="text"
            id="business"
            name="business"
            value={formData.business}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input
            type="text"
            id="location"
            name="location"
            value={formData.location}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="salesVolumeEUR">Sales Volume (EUR)</label>
          <input
            type="number"
            id="salesVolumeEUR"
            name="salesVolumeEUR"
            value={formData.salesVolumeEUR}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="resultEUR">Result (EUR)</label>
          <input
            type="number"
            id="resultEUR"
            name="resultEUR"
            value={formData.resultEUR}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="employees">Employees</label>
          <input
            type="number"
            id="employees"
            name="employees"
            value={formData.employees}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="shareToBeTransferred">Share to Be Transferred</label>
          <input
            type="text"
            id="shareToBeTransferred"
            name="shareToBeTransferred"
            value={formData.shareToBeTransferred}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="transactionBackground">Transaction Background</label>
          <textarea
            id="transactionBackground"
            name="transactionBackground"
            value={formData.transactionBackground}
            onChange={handleChange}
            rows="4"
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="productMarketFit">Product Market Fit</label>
          <input
            type="text"
            id="productMarketFit"
            name="productMarketFit"
            value={formData.productMarketFit}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="valueProposition">Value Proposition</label>
          <textarea
            id="valueProposition"
            name="valueProposition"
            value={formData.valueProposition}
            onChange={handleChange}
            rows="4"
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="profitMargin">Profit Margin</label>
          <input
            type="number"
            id="profitMargin"
            name="profitMargin"
            value={formData.profitMargin}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="revenue">Revenue</label>
          <input
            type="number"
            id="revenue"
            name="revenue"
            value={formData.revenue}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="cashflow">Cashflow</label>
          <input
            type="number"
            id="cashflow"
            name="cashflow"
            value={formData.cashflow}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="customerBase">Customer Base</label>
          <textarea
            id="customerBase"
            name="customerBase"
            value={formData.customerBase}
            onChange={handleChange}
            rows="4"
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="companyCulture">Company Culture</label>
          <textarea
            id="companyCulture"
            name="companyCulture"
            value={formData.companyCulture}
            onChange={handleChange}
            rows="4"
            required
          ></textarea>
        </div>
        <button type="submit" className="submit-btn">
          Save Profile
        </button>
      </form>
    </div>
  );
};

export default AboutYourBusiness;
