// React Component (Reports.js)

import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axiosInstance from '../Auth/AxiosInstance';

const API_URL = process.env.REACT_APP_API_URL; // Your API base URL

function Reports() {
    const [selectedFile, setSelectedFile] = useState(null);
    const [files, setFiles] = useState([]);

    const {roomNumber} = useParams();

    const fetchFiles = async () => {
        const response = await axiosInstance.get(`${API_URL}/api/room/${roomNumber}/reports`);
        setFiles(response.data);
    };

    useEffect(() => {
        // Fetch files associated with the room

        fetchFiles();
    }, [roomNumber]);

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    const handleFileUpload = async () => {
        if (!selectedFile) return;

        const formData = new FormData();
        formData.append('file', selectedFile);

        await axiosInstance.post(`${API_URL}/api/room/${roomNumber}/upload-report`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' },
        });

        // Refresh the file list after upload
        fetchFiles();
        setSelectedFile(null);
    };

    return (
        <div style={{ textAlign: 'center' }}>
            <h1>Reports</h1>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleFileUpload}>Upload Report</button>
            <div>
                <h2>Uploaded Reports</h2>
                {files.map((file) => (
                    <div key={file._id}>
                        <a href={`/upload-reports/${file.filename}`} target="_blank" rel="noopener noreferrer">
                            {file.filename}
                        </a>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Reports;
