// React Component (Reports.js)

import React, { useState, useEffect } from 'react';
import axiosInstance from '../Auth/AxiosInstance';
import './styles/Reports.css'; // Import the CSS file

const API_URL = process.env.REACT_APP_API_URL; // Your API base URL

function Reports({ roomNumber }) {
    const [selectedFile, setSelectedFile] = useState(null);
    const [files, setFiles] = useState([]);

    const fetchFiles = async () => {
        const response = await axiosInstance.get(`${API_URL}/api/room/${roomNumber}/reports`);
        setFiles(response.data);
    };

    useEffect(() => {
        fetchFiles();
    }, [roomNumber]);

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    const handleFileUpload = async () => {
        if (!selectedFile) return;

        const formData = new FormData();
        formData.append('file', selectedFile);

        await axiosInstance.post(`${API_URL}/api/room/${roomNumber}/upload-report`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' },
        });

        fetchFiles();
        setSelectedFile(null);
    };

    const handleFileDelete = async (fileId) => {
        await axiosInstance.delete(`${API_URL}/api/room/${roomNumber}/reports/${fileId}`);
        setFiles(files.filter((file) => file._id !== fileId));
    };

    return (
        <div className="report-container">
            <div className="upload-section">
                <input type="file" onChange={handleFileChange} className="file-input" />
                <button onClick={handleFileUpload} className="upload-button">
                    Upload Report
                </button>
            </div>
            <div className="file-list">
                <h3 className="file-list-title">Uploaded Reports</h3>
                {files.map((file) => (
                    <div key={file._id} className="file-item">
                        <a
                            href={`${API_URL}/${file.path}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="file-link"
                        >
                            {file.filename}
                        </a>
                        <button
                            onClick={() => {
                                if (window.confirm("Are you sure you want to delete this file?")) {
                                    handleFileDelete(file._id);
                                }
                            }}
                            className="delete-button"
                        >
                            Delete
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Reports;
